import React, { useEffect, useRef } from "react";

const LoopGallery = () => {
  const galleryRef = useRef(null);

  // Example array of image URLs (you can replace this with dynamic data)
  const images = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,"
  ];

  // Function to handle scroll position and loop
  const handleScroll = () => {
    const gallery = galleryRef.current;
    const { scrollTop, scrollHeight, clientHeight } = gallery;

    // If the user reaches the bottom of the gallery, reset to the start
    if (scrollTop + clientHeight >= scrollHeight) {
      gallery.scrollTop = 0; // Reset scroll to the start
    }

    // If the user is near the top, reset to the end (looping back)
    if (scrollTop === 0) {
      gallery.scrollTop = scrollHeight; // Reset scroll to the end
    }
  };

  useEffect(() => {
    const gallery = galleryRef.current;

    // Add scroll event listener
    gallery.addEventListener("scroll", handleScroll);

    return () => {
      gallery.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="overflow-hidden h-full">
      <div
        className="flex flex-col space-y-4 overflow-y-auto scroll-smooth py-2"
        ref={galleryRef}
      >
        {images.concat(images).map((image, index) => (
          <div key={index} className="flex-shrink-0">
            <img
              src={image}
              alt={`Image ${index}`}
              className="w-72 h-48 rounded-lg"
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default LoopGallery;
